let particles = {};
let newParticles = [];

function push(p) {
    newParticles.push(p);
} 
function processPushes() {
    for (let i = 0; i < newParticles.length; i++) {
        let p = newParticles[i];
        if (!particles[p.color]) particles[p.color] = [];
        particles[p.color].push(p);
    }
    newParticles = [];
}

